{
  data() {
    return {
      filters: {
        _level: q('_level') || 1,
        region: q('region'),
        uuid: q('uuid'),
        district: q('district'),
        area: q('area'),
        search: q('search'),
        year: q('year'),        
        basis: q('basis'),
        tab_index: q('tab_index') || 1,
        org_id: q('org_id'),
        table_name: q('table_name') || 'accounts_turnover_v2',
      },
      tab_index: q('tab_index') || 0,
      table_def: ['accounts_turnover_v2', 'balance_sheet', 'info'],
      tables: [{
          id: 'accounts_turnover_v2',
          name: this.$l('analytical-accounts', 'аналитик счётлар'),
        	index: 1
        },{
          id: 'accounts_turnover_parent',
          name: this.$l('accounts-turnover-parent', 'счётлар бўйича'),
          index: 1
        },{
          id: 'accounts_turnover_groups',
          name: this.$l('accounts-turnover-groups', 'гуруҳланган счётлар'),
          index: 1
        },{
          id: 'balance_sheet',
          name: this.$l('balance-sheet', 'бухгалтерия баланси'),
          index: 2
        },{
          id: 'financial_result',
          name: this.$l('financial-result', 'молиявий натижа'),
          index: 2
        },{
          id: 'currency_result',
          name: this.$l('currency-rate', 'валюта курси'),
          index: 2
        },{
          id: 'info',
          name: this.$l('by-organization', 'ташкилот бўйича'),
          index: 3
        },{
          id: 'accounts_turnover_balance',
          name: this.$l('by-contract', 'шартнома бўйича'),
          index: 3
        }],
      def:  this.$l('analytical-accounts', 'аналитик счётлар'),
      def2: this.$l('balance-sheet', 'бухгалтерия баланси'),
      def3: this.$l('by-organization', 'ташкилот бўйича'),
    }
  },
  watch: {
    $route(to, from){
			this.table_name = to.query.table_name,
			this.tab_index = to.query.tab_index,
			this.district = to.query.district,
			this.org_id = to.query.org_id,
			this.search = to.query.search,
			this._level = to.query._level,
			this.region = to.query.region,
			this.uuid = to.query.uuid,
			this.basis = to.query.basis,
			this.area = to.query.area,
			this.year = to.query.year
    }
  },
  computed: {
    getFilters() {
      return {
        ...this.filters
      }
    }
  },
  mounted(){
    // if(this.$user.extraData.org_id != 305969048){
    //   this.tables = this.tables.filter(v => v.id != 'currency_result');
    // }
    if(this.$user.extraData.org_type == 99){
      window.location.href="https://keeping.uz/tables/agent_steps"
    }
  },
  methods: {
    changeRadio(name){
      this.table_def[this.tab_index] = name;
      this.filters.table_name = name;
      this.$router.replace({
        query: {
          ...this.filters
        }
      });
    },
  	setType(ind){
			this.filters.table_name = this.table_def[ind-1];
      this.tab_index = ind-1;
      this.filters.tab_index = ind-1;
      this.$router.replace({
        query: {
          ...this.filters
        }
      })
    },
		onTableFilterChanged(filters) {
      this.filters = {...filters};
    }
  }
}

