{
    data() {
            return {
                is_sidebar: false,
                is_scrolled: false,
                page_name: this.$route.params.name,
                nav_items: [{
                        id: 1,
                        link: "#opportunities",
                  			current_section: "opportunities",
                        class_name: "item_opportunities",
                        title: this.$l("language.available-opportunities", "Mavjud imkoniyatlar")
                    },
                    // {
                    //     id: 2,
                    //     link: "#works",
                    //     current_section: "works",
                    //     class_name: "item_works",
                    //     title: this.$l("language.how-it-works", "Qanday ishlaydi")
                    // },
                    {
                        id: 3,
                        link: "#advantages",
                        current_section: "advantages",
                        class_name: "item_advantages",
                        title: this.$l("language.advantages", "Afzalliklar")
                    },
                    {
                        id: 4,
                        link: "#thoughts",
                        class_name: "item_thoughts",
                        title: this.$l("language.customer-opinion", "Mijozlar fikri")
                    },
                    {
                        id: 5,
                        link: "#ink-faq",
                        class_name: "item_faq",
                        title: this.$l("language.faq-headline", "FAQ")
                    },
                    {
                        id: 6,
                        link: "tel:+998883018484",
                        class_name: "item_contact",
                        title: "+998 (88) 301-84-84"
                    }
                ],
             		ref_id: null,
            }
        },
        computed: {
            isMainPage() {
                return this.$route.name == 'home';
            }
        },
        mounted() {
            ["mousewheel", "scroll"].forEach((evt) => window.addEventListener(evt, this.handleScroll, false));
            this.determinePage();
          	this.checkGetToTheLink();


          const urlParams = new URLSearchParams(window.location.search);
          this.ref_id = urlParams.get('ref_id');
          if(this.ref_id) this.markRefId()

        },
        methods: {
          	markRefId(){
            	if(this.ref_id) this.$api.get(`/v1/marketing/view/count?ref_id=${this.ref_id}`)
            },
            // THIS FUNCTION FOR DETERMINE PAGE FOR ADD ACTIVE CLASS !
            determinePage() {
              	const current_link = document.querySelector(".item_thoughts");
                this.page_name == "ink-thoughts" ? current_link.classList.add("ActiveItem") : current_link.classList.remove("ActiveItem");
            },
            // THIS FUNCTION FOR OPEN OR CLOSE SIDEBAR WHEN HAMBURGER MENU CLICKED !
            toggleSidebar() {
                this.is_sidebar = !this.is_sidebar;
            },
            // THIS FUNCTION FOR BIND CLASS WHEN WINDOW SCROLLED !
            handleScroll() {
                let screenWidth = window.innerWidth;
                if (screenWidth < 577) return;
                if (window.pageYOffset > 115) {
                    this.is_scrolled = true;
                } else if (this.is_scrolled && window.pageYOffset <= 115 && window.pageYOffset > 0) {
                    return;
                } else {
                    this.is_scrolled = false;
                }
            },
            getToTheLink(id) {
                if (this.isMainPage) {
                    let element = document.getElementById(id);
                    let targetPosition = element.getBoundingClientRect().top;
                    window.scrollTo({
                        top: targetPosition,
                        behavior: "smooth"
                    })
                } else {
                    this.$router.push({
                        path: '/',
                        query: {
                            getTo: id
                        }
                    });
                }
            },
            // THIS FUNCTION FOR CHECK LINK !
            checkGetToTheLink() {
                let theLink = this.$route.query.getTo;
                if (!theLink) return;
                setTimeout(() => {
                    this.getToTheLink(theLink)
                    this.$router.replace({
                        path: this.$route.path
                    });
                }, 500)
            }
        }
}

components.css:
/********** STYLES **********/
.header__parts {
    gap: 8px;
    display: flex;
    align-items: center;
    padding: 40px 0px 14px 0px;
    justify-content: space-between;
}
.header__parent {
    top: 0;
    z-index: 5;
    width: 100%;
    position: fixed;
    transition: var(--transition);
    background: var(--color-white);
}
.header__hill {
    height: 124px;
}
.header__navigation {
    width: 70%;
/*     padding: 23.5px 0px; */
    border-radius: 50px;
/*     background: var(--color-grey); */
}
.header__navbar {
  	gap: 8px;
    display: flex;
    align-items: center;
    justify-content: space-between;
}
.header__link {
    font-size: 18px;
    font-weight: 600;
    text-align: left;
    width: max-content;
    line-height: 21.47px;
    letter-spacing: 0.04em;
    color: var(--color-black);
    transition: var(--transition);
    font-family: font_gilroy_light;
    text-decoration: none !important;

  	flex: 1;
  	padding: 23.5px;
  	text-align: center;
    border-radius: 50px;
  	background: var(--color-grey);
}
.header__logo {
    width: 15%;
    border-radius: 50px;
    padding: 16px 175px 16px 35px;
    background: var(--color-grey);
  	padding: 16px 0px;


    background: var(--color-cream);
    box-shadow: 0px 0px 6px var(--color-blue);
}
.header__intrance {
    gap: 8px;
    width: 15%;
    display: flex;
    position: relative;
    align-items: center;
}
.header__login {
    width: 340px;
    display: block;
    font-size: 18px;
    font-weight: 600;
    text-align: center;
    padding: 23.5px 0px;
    border-radius: 50px;
    line-height: 21.47px;
    letter-spacing: 0.04em;
    color: var(--color-white);
    background: var(--color-blue);
    transition: var(--transition);
    font-family: font_gilroy_light;
    text-decoration: none !important;
    border: 1px solid var(--color-blue);
}
.header__login-icon {
    width: 50px;
    height: 50px;
    display: none;
}
.header__hamburger {
    width: 50px;
    height: 50px;
    display: none;
    cursor: pointer;
    position: relative;
    border-radius: 5px;
}
.header__hamburger .header__hamburger-area {
    width: 50px;
    height: 50px;
    transition: var(--transition);
}
.header__hamburger span {
    left: 12px;
    width: 50%;
    height: 3px;
    position: absolute;
    border-radius: 3px;
    display: inline-block;
    background: var(--color-blue);
    transition: var(--transition);
    &:nth-of-type(1) {
        top: 16px;
    }
    &:nth-of-type(2) {
        top: 24px;
    }
    &:nth-of-type(3) {
        top: 32px;
    }
}
.header__hamburger.active .header__hamburger-area {
    transform: rotate(360deg);
}
.header__hamburger.active span {
    &:nth-of-type(1) {
        top: 18px;
        left: 14px;
        width: 45%;
        transform: translateY(6px) rotate(-45deg);
    }
    &:nth-of-type(2) {
        opacity: 0;
    }
    &:nth-of-type(3) {
        top: 30px;
        width: 45%;
        left: 14px;
        transform: translateY(-6px) rotate(45deg);
    }
}
.header__link {
    transition: .3s ease all;
    color: var(--color-darkgrey);
}
.header__img {
  	margin: auto;
}
.header__link-txt {
    color: #000;
    font-size: 18px;
    font-weight: 400;
    line-height: 120%;
    position: relative;
    white-space: nowrap;
    display: inline-block;
    transform-origin: 50% 0;
    transform-style: preserve-3d;
    transition: transform 0.3s, -webkit-transform 0.3s;
}
.header__link-txt::before {
    left: 0;
    top: 100%;
    width: 100%;
    height: 100%;
    position: absolute;
    transform-origin: 50% 0;
    color: var(--color-blue);
    content: attr(data-text);
    transform: rotateX(-90deg);
    -ms-transform-origin: 50% 0;
    -webkit-transform-origin: 50% 0;
    -webkit-transform: rotateX(-90deg);
}
.header__link:hover .header__link-txt {
    transform: rotateX(90deg);
    -webkit-transform: rotateX(90deg);
}
.header__link:hover {
    color: var(--color-blue);
}
/********** BINDING **********/
.ActiveHeader {
    box-shadow: 0px 0px 10px var(--color-blue);
}
.ActiveSidebar {
    transform: translatex(0%) !important;
}
.ActiveItem {
  	color: var(--color-blue);
}
/********** SELECTORS **********/
.header__link:hover {
    color: var(--color-blue);
}
.header__login:hover {
    color: var(--color-blue);
    background: var(--color-white);
}
/********** RESPONSIVE **********/
@media(max-width: 1470px) {
    .header__link,
    .header__login {
        font-size: 14px;
    }
    .header__navigation {
        left: 0;
        right: 0;
        margin: 0;
        bottom: 0;
        top: 124px;
        width: 100%;
        z-index: 12;
        height: 100vh;
        display: grid;
        overflow: auto;
        position: fixed;
        padding-top: 60px;
        border-radius: 0px;
        align-items: flex-start;
        -webkit-transition: 0.2s;
        backdrop-filter: blur(35px);
        transform: translateX(-100%);
    }
    .header__hamburger {
        display: block;
    }
    .header__navbar {
        gap: 20px;
        flex-direction: column;
    }
    .header__link {
        font-size: 18px;
    }
    .header__login {
        display: none;
    }
    .header__login-icon {
        display: block;
    }
    .header__intrance {
        justify-content: end;
    }
}
@media(max-width: 768px) {
  	.header__logo {
   	 	box-shadow: none;
   	 	background: transparent;
  	}
    .header__parts {
        gap: 0px;
    }
    .header__intrance {
        width: max-content;
    }
}
@media(max-width: 400px) {
    .header__parts {
        padding: 10px 0px;
    }
    .header__logo {
        padding: 0px;
        background: none;
    }
    .header__navigation {
        top: 70px;
    }
    .header__hill {
        height: 50px;
    }
}
@media(max-width: 320px) {
    .header__intrance {
        gap: 0px;
    }
    .header__img {
        width: 100px;
    }
    .header__login-icon {
        width: 25px;
    }
}

