components.js:
{
    data() {
        return {
            inputValue: null,
            inputPrefix: "so'm",
        };
    },
    props: {
        value: {
            type: Number,
            default: NaN,
        },
        maxValue: {
            type: Number,
            default: null,
        },
        placeholder: {
            type: String,
            default: "Сумма",
        },
        isReadonly: {
            type: Boolean,
            default: false,
        },
        isText: {
            type: Boolean,
            default: false,
        },
    },
    mounted() {
        this.inputProps();
    },
    watch: {
        value: {
            immediate: true,
            handler(val) {
                // console.log(val, oldVal);
                this.inputValue = val || val === 0 ? this.formatNumber(val) : "";
                if (val) this.$emit("modified", parseInt(val));
            },
        },
    },
    methods: {
        inputNumber(eve) {
            let data = parseInt(eve.target.value.replace(/\D/g, ""));

            if (this.maxValue && data > this.maxValue) {
                this.inputValue = this.formatNumber(this.maxValue);
                this.$emit("modified", parseInt(this.maxValue));
            } else {
                this.inputValue = data ? this.formatNumber(data) : "";
                if (data) this.$emit("modified", parseInt(data));
            }
        },

        inputKey(event) {
            const blockedKeys = ["ArrowLeft", "ArrowRight", "Home", "End", "Backspace"];
            if (blockedKeys.includes(event.key)) event.preventDefault();
            if (event.key === "Backspace") {
                let data = parseInt(event.target.value.replace(/\D/g, "").slice(0, -1));
                this.inputValue = data ? this.formatNumber(data) : "";
                this.$emit("modified", parseInt(data || 0));
            }
        },

        inputProps() {
            if (this.value) this.inputValue = this.formatNumber(this.value);
        },

        formatNumber(x) {
            // return x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ") + " " + this.inputPrefix;
            return x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ");
        },
    },
}

