{
    		data() {
            return {
                news: []
            }
        },
        computed: {
            currentLanguage() {
                return localStorage.getItem("platon_locale");
            }
        },
        mounted() {
            this.getNews();
            this.newsSwiper();
        },
        methods: {
            // THIS FUNCTION FOR GET NEWS FROM BASE !
            getNews() {
                try {
                    this.$api.get(`v1/kp/landing/get/news?locale=${this.currentLanguage}`, {
                        useAuthorization: false
                    }).then(response => {
                        this.news = response.data.data;
                    })
                } catch (error) {
                    console.log("ink-section-news, getNews()", error);
                }
            },
            // THIS FUNCTION FOR WORK NEWS SWIPER !
            newsSwiper() {
                this.addStyle("/media/store/swiper/swiper-bundle.min.css");
                new window.Swiper(".ink-news__swiper", {
                    direction: "horizontal",
                    spaceBetween: 30,
                    slidesPerView: 2,
                    speed: 3000,
                    breakpoints: {
                        0: {
                            slidesPerView: 1
                        },
                        1200: {
                            slidesPerView: 2
                        }
                    },
                    autoplay: {
                        delay: 100,
                        pauseOnMouseEnter: true,
                        disableOnInteraction: true
                    },
                    loop: true
                })
            },
            // THIS FUNCTION FOR HIGHLIGHT LAST WORD OF THE SENTENCE !
            highlightedLastWord(sentence) {
                const words = sentence.split(' ');
                const lastWord = words.pop();
                const sentenceWithoutLastWord = words.join(' ');
                return `${sentenceWithoutLastWord} <span class="highlighted">${lastWord}</span>`;
            }
        }
}

