components.js:
{
  scripts: ['https://keeping.uz/services/platon-core/web/v1/store/file/js/highcharts.js',
            'https://keeping.uz/services/platon-core/web/v1/store/file/js/accessibility.js'
           ],
  props: ['bdate', 'edate'],
  data() {
    return {
      chartData: null,
      fetchChartDataTimer: null,
      isLoading: false
    }
  },  
  mounted(){
    this.fetchChartData()
  },
  watch: {
    bdate(newValue) {
      if(newValue) this.debounceFetchChartData()
    },
    edate(newValue) {
     	if(newValue) this.debounceFetchChartData()
    },
  },
  methods: {
    debounceFetchChartData() {
      if (this.fetchChartDataTimer) {
        clearTimeout(this.fetchChartDataTimer)
      }

      this.fetchChartDataTimer = setTimeout(() => {
        this.fetchChartData()
      }, 400)
    },

    fetchChartData() {
      const bdate = this.bdate || this.getFirstDayOfMonth()
      const edate = this.edate || this.getTodayDate()

      if (!this.isValidDateFormat(bdate) || !this.isValidDateFormat(edate)) {
        console.error('Noto\'gri format')
        return
      }

      const daysDifference = this.calculateDaysDifference(bdate, edate)

      if (daysDifference <= 30) {
        this.isLoading = true

        this.$api.get(`/v1/dashboard/monitoring?bdate=${bdate}&edate=${edate}`)
          .then((res) => {
           if (res?.data?.data && Array.isArray(res.data.data.data) && res.data.data.data.length > 0) {
              this.chartData = res.data.data.data

              this.$nextTick(() => {
                this.renderChart()
              })
            } else {
              console.error('No data received or data is invalid')
              this.$bvToast.toast('Оралиқ нотўгри форматда', {
                title: 'Хатолик',
                variant: 'danger',
              })
            }
          })
          .catch((error) => {
            console.error('Error fetching chart data:', error)
          })
        	.finally(() => {
            this.isLoading = false
          })
      } else {
        this.$bvToast.toast('Оралиқ 30 кундан катта бўлмаслиги керак', {
          title: 'Хатолик',
          variant: 'danger',
        })
        console.error(`Oraliq katta: ${daysDifference} kun. Maksimal oraliq 30 kun`)
      }
    },

    calculateDaysDifference(startDate, endDate) {
      const start = new Date(startDate)
      const end = new Date(endDate)

      const timeDifference = Math.abs(end.getTime() - start.getTime())

      return Math.ceil(timeDifference / (1000 * 3600 * 24))
    },

    getFirstDayOfMonth() {
      const now = new Date()
      return `${now.getFullYear()}-${String(now.getMonth() + 1).padStart(2, '0')}-01`
    },

    getTodayDate() {
      const now = new Date()
      return `${now.getFullYear()}-${String(now.getMonth() + 1).padStart(2, '0')}-${String(now.getDate()).padStart(2, '0')}`
    },

    isValidDateFormat(dateString) {
      const dateFormatRegex = /^\d{4}-\d{2}-\d{2}$/

      if (!dateString || !dateFormatRegex.test(dateString)) {
        return false
      }

      const date = new Date(dateString)
      return date instanceof Date && !isNaN(date)
    },

    renderChart() {
      if (!this.chartData || !Array.isArray(this.chartData) || this.chartData.length === 0) {
        console.warn('Cannot render chart: No valid data available')
        return
      }

      const container = document.getElementById('container')
      if (!container) {
        console.error('Chart container not found')
        return
      }

      try {
        Highcharts.chart('container', {
          chart: {
            type: 'spline'
          },
          title: {
            // text: 'Кунлик фойдаланувчилар сони'
            text: null
          },
          xAxis: {
            categories: this.chartData.map(item => item.day),
             accessibility: {
              rangeDescription: 'Сана'
            },
          },
          yAxis: {
            title: {
              text: 'Сони'
            },
            accessibility: {
              rangeDescription: 'Сони'
            },
          },
          series: [
            {
              name: 'Аккаунт очганлар',
              data: this.chartData.map(item => item.auth_count)
            },
            {
              name: 'Офертани қабул қилганлар',
              data: this.chartData.map(item => item.oferta_count)
            },
            {
              name: 'Модул сотиб олганлар',
              data: this.chartData.map(item => item.modul_count)
            },
            {
              name: 'Счёт берилган',
              data: this.chartData.map(item => item.code_count)
            }
          ],
          plotOptions: {
            spline: {
              marker: {
                enabled: true
              }
            }
          }
        })
      } catch (error) {
        console.error('Error rendering chart:', error)
      }
    },
    formatDate(dateString) {
      try {
        const date = new Date(dateString)
        return date.toLocaleDateString('ru-RU')
      } catch (e) {
        return dateString
      }
    },
  },
  beforeDestroy() {
    if (this.fetchChartDataTimer) {
      clearTimeout(this.fetchChartDataTimer)
    }
  }
}

