components.js:
{
  	data() {
		return {
			formData: {
				fullName: "",
				phone: "",
				message: "",
			},
			isLoading: false,
			successMessage: "",
			errorMessage: "",
			API_URL: "v1/landing/post/contacts",
		};
	},
	methods: {
		async submitForm() {
			this.successMessage = "";
			this.errorMessage = "";
			this.isLoading = true;

			try {
				if (
					!this.formData.fullName ||
					!this.formData.phone ||
					!this.formData.message
				) {
					throw new Error(this.$l('landing.contact.form.message.error.required', "Barcha maydonlarni to'ldiring"));
				}

				await this.$api.post(this.API_URL, {
						full_name: this.formData.fullName,
						phone_number: this.formData.phone,
						your_message: this.formData.message,
						timestamp: new Date().toISOString(),
				}).then(()=>{
          this.successMessage = this.$l('landing.contact.form.message.success', 'Xabaringiz muvaffaqiyatli yuborildi!');

          this.formData.fullName = "";
          this.formData.phone = "";
          this.formData.message = "";
        });

			} catch (error) {
				this.errorMessage =
					error.message || this.$l('landing.contact.form.message.error', "Xabar yuborishda xatolik yuz berdi");
				console.error("Form submission error:", error);
			} finally {
				this.isLoading = false;
			}
		},
	},
}

