    const authorizedIds = [
      '56b7a2dc-046d-43e7-b982-843bbc8b5ca9',
      'a74234f7-c1f8-445d-b057-dadf9b59efed',
      '44d5af40-5842-430b-9c6c-2fec3a4a99ca',
      'ca8e91e2-64e6-4a00-947b-8eca5731963a',
      '00fcfbb2-f226-4177-a712-d51bf9774081'
    ];
    return authorizedIds.includes(this.$user?.extraData?.user_id);
  }
},
methods: {
  toggleChat() {
    this.isOpen = !this.isOpen;
    if (this.isOpen) {
      this.loadChatHistory(true); // Always load history when chat opens
    }
  },

  showWelcomeMessage() {
    const welcomeMessage = {
      id: Date.now(),
      type: 'AI',
      text: this.$l('chatbot.intro.text', 'Ассалому алайкум! Keeping платформасига хуш келибсиз! Хизматларимиз билан боғлиқ ҳар қандай саволларингизга жавоб  беришга тайёрман. Сизга қандай ёрдам бера оламан?'),
      timestamp: new Date()
    };
    this.messages.push(welcomeMessage);
    this.$nextTick(() => {
      this.scrollToBottom();
    });
  },

  convertMarkdownLinksToHtml(markdown) {
    return markdown.replace(
      /\[([^\]]+)\]\((https?:\/\/[^\s)]+)\)/g,
      '<a href="$2" target="_blank" rel="noopener noreferrer" style="text-decoration: underline;">$1</a>'
    );
  },

  autoLink(text) {
    return text.replace(
      /(https?:\/\/[^\s]+)/g,
      '<a href="$1" target="_blank" rel="noopener noreferrer">$1</a>'
    );
  },

  async loadChatHistory(isInitialLoad = false) {
    if (!this.user_id) {
      console.warn('No user_id, skipping loadChatHistory');
      this.showWelcomeMessage();
      return;
    }

    this.isLoadingHistory = true;

    try {
      const response = await this.$api.get(`/v1/openai/chat/history?limit=${this.limit}`);

      if (response.data && response.data.data && response.data.data.length > 0) {
        const historyMessages = response.data.data.map(msg => ({
          id: msg.id || Date.now() + Math.random(),
          type: msg.role,
          text: msg.content,
          timestamp: new Date(msg.sent_at)
        }));

        this.messages = historyMessages;
        this.hasMoreMessages = response.data.data.length === this.limit;

        if (isInitialLoad) {
          this.$nextTick(() => {
            this.scrollToBottom();
          });
        }
      } else {
        this.messages = [];
        this.hasMoreMessages = false;
        this.showWelcomeMessage();
      }
    } catch (error) {
      console.error('Error loading chat history:', error);
      this.messages = [];
      this.hasMoreMessages = false;
      this.showWelcomeMessage();
    } finally {
      this.isLoadingHistory = false;
    }
  },

  async loadMoreHistory() {
    const container = this.$refs.messagesContainer;
    let scrollHeightBefore = container ? container.scrollHeight : 0;
    let scrollTopBefore = container ? container.scrollTop : 0;
    const isAtBottom = container && scrollTopBefore + container.clientHeight >= scrollHeightBefore;

    this.limit = this.limit + 10;
    await this.loadChatHistory();

    if (container) {
      this.$nextTick(() => {
        if (isAtBottom) {
          this.scrollToBottom();
        } else {
          const scrollHeightAfter = container.scrollHeight;
          const scrollDifference = scrollHeightAfter - scrollHeightBefore;
          container.scrollTop = scrollTopBefore + scrollDifference;
        }
      });
    }
  },

  async sendMessage() {
    if (!this.currentMessage.trim() || this.isLoading || !this.user_id) return;

    const userMessage = {
      id: Date.now(),
      type: 'USER',
      text: this.currentMessage,
      timestamp: new Date()
    };

    this.messages.push(userMessage);
    const messageToSend = this.currentMessage;
    this.currentMessage = '';
    this.isLoading = true;

    this.$nextTick(() => {
      this.scrollToBottom();
    });

    try {
      const { data } = await this.$api.post('/v1/openai/chat', {
        message: messageToSend
      });

      if (data.data.success && data.data.data.reply) {
        const botMessage = {
          id: Date.now() + 1,
          type: 'AI',
          text: data.data.data.reply,
          timestamp: new Date()
        };
        this.messages.push(botMessage);
      } else {
        throw new Error('Invalid AI response');
      }
    } catch (error) {
      console.error('Error sending message:', error);
      const errorMessage = {
        id: Date.now() + 1,
        type: 'bot',
        text: 'Sorry, there was an error processing your message. Please try again.',
        timestamp: new Date()
      };
      this.messages.push(errorMessage);
    } finally {
      this.isLoading = false;
      this.$nextTick(() => {
        this.scrollToBottom();
      });
    }
  },

  scrollToBottom() {
    const container = this.$refs.messagesContainer;
    if (container) {
      container.scrollTop = container.scrollHeight;
    } else {
      console.warn('messagesContainer ref is not available');
    }
  },

  formatTime(timestamp) {
    return new Date(timestamp).toLocaleTimeString([], {
      hour: '2-digit',
      minute: '2-digit'
    });
  }
}
}

