{
    		data() {
            return {
                thoughts: [],
              	hasUser: false
            }
        },
        mounted() {
            this.getThoughts();
            this.thoughtsSlider();
          	this.determineUser();
        },
        methods: {
          	// THIS FUNCTION FOR DETERMINE USER OR ORDINARY USER !
          	determineUser() {
              if(localStorage.getItem("auth_user")) {
                this.hasUser = true;
              } else {
                this.hasUser = false;
              }
            },
            // THIS FUNCTION FOR HIGHLIGHT LAST WORD OF THE SENTENCE !
            highlightedLastWord(sentence) {
                const words = sentence.split(' ');
                const lastWord = words.pop();
                const sentenceWithoutLastWord = words.join(' ');
                return `${sentenceWithoutLastWord} <span class="highlighted">${lastWord}</span>`;
            },
            // THIS FUNCTION FOR GET THOUGHTS FROM BASE !
            getThoughts() {
                try {
                    this.$api.get("v1/landing/get/thoughts", {
                        useAuthorization: false
                    }).then(response => {
                        this.thoughts = response.data.data;
                    })
                } catch (error) {
                    console.log("section-thoughts, getThoughts()", error);
                }
            },
            // THIS FUNCTION FOR WORK SLIDER !
            thoughtsSlider() {
                this.addStyle("/media/store/swiper/swiper-bundle.min.css");
                new window.Swiper(".swiper-thoughts", {
                    direction: "horizontal",
                    spaceBetween: 16,
                    slidesPerView: 2,
                    speed: 2000,
                  	breakpoints: {
                      0: {
                        slidesPerView: 1
                      },
                      992: {
                        slidesPerView: 2
                      }
                    },
                    autoplay: {
                        delay: 100,
                        pauseOnMouseEnter: true,
                        disableOnInteraction: true
                    },
                    loop: true
                })
            }
        }
}

