components.js:
{
    		data() {
            return {
                faqs: [],
                search_term: ""
            }
        },
        mounted() {
            this.getFaq();
        },
        computed: {
          	// THIS FUNCTION RETURNS CURRENT LANGUAGE IN WEBSITE !
            currentLanguage() {
                return localStorage.getItem('platon_locale');
            },
            // THIS FUNCTION FOR SHOW FAQ WHEN FAQ WILL SEARCH WITH INPUT AND IT WORKS MOUNTED !
            filteredQuestions() {
                return this.faqs.filter(entry => {
                    return entry.question.toLowerCase().includes(this.search_term.toLowerCase()) ||
                      		 entry.answer.toLowerCase().includes(this.search_term.toLowerCase());
                });
            }
        },
        methods: {
            // THIS FUNCTION FOR GET FAQ FROM BASE !
            getFaq() {
                try {
                    this.$api.get(`v1/kp/landing/get/faq?locale=${this.currentLanguage}`, {
                        useAuthorization: false
                    }).then(response => {
                        this.faqs = response.data.data.slice(0, 5);
                    })
                } catch (error) {
                    console.log("ink-section-faq, getFaq()", error);
                }
            },
            // THIS FUNCTION FOR HIGHLIGHT LAST WORD OF THE SENTENCE !
            highlightedLastWord(sentence) {
                const words = sentence.split(' ');
                const lastWord = words.pop();
                const sentenceWithoutLastWord = words.join(' ');
                return `${sentenceWithoutLastWord} <span class="highlighted">${lastWord}</span>`;
            },
            // THIS FUNCTION FOR OPEN OR CLOSE WHEN FAQ WILL BE CLICK !
            toggleFaq(value) {
                value.is_open = !value.is_open;
            }
        }
}

