  data() {
      return {
        full_name: null,
        phone_number: null,
        your_email: null,
        your_message: null
      }
    },
    methods: {
      // THIS FUNCTION FOR HIGHLIGHT LAST WORD OF THE SENTENCE !
      highlightedLastWord(sentence) {
        const words = sentence.split(' ');
        const lastWord = words.pop();
        const sentenceWithoutLastWord = words.join(' ');
        return `${sentenceWithoutLastWord} <span class="highlighted">${lastWord}</span>`;
      },
      // FOR SHOW DANGER TOAST !
      makeDangerToast(variant = "danger") {
        this.$bvToast.toast(this.$l("language.kp-toast-danger", "Ma'lumotlarni to'liq kiriting !"), {
          variant: variant,
          solid: true
        })
      },
      // FOR SHOW SUCCESS TOAST !
      makeSuccessToast(variant = "success") {
        this.$bvToast.toast(this.$l("language.kp-toast-success", "Muvaffaqiyatli yuborildi !"), {
          variant: variant,
          solid: true
        })
      },
      // FOR CLEAN VALUES !
      cleanValues() {
        this.full_name = null;
        this.phone_number = null;
        this.your_email = null;
        this.your_message = null;
      },
      // FOR VALIDATION !
      checkValidation() {
        if (this.full_name != null && this.phone_number != null && this.your_email != null && this.your_message != null) {
          this.sendInfos();
        } else {
          this.makeDangerToast();
        }
      },
      // FOR POST INFOS TO BASE !
      sendInfos() {
        this.$api.post(`v1/landing/post/contacts`, {
          full_name: this.full_name,
          phone_number: this.phone_number,
          your_email: this.your_email,
          your_message: this.your_message
        }).then(response => {
          console.log("response.data.data", response.data.data);
          if (response.status == 200) {
            this.cleanValues();
            this.makeSuccessToast();
          }
        })
      }
    }
}

