	display: flex;
	flex-direction: column;
	align-items: flex-start;
	justify-content: center;
	gap: 0.75rem;
}

.landing-hero-title {
	font-size: 1.5rem; /* text-2xl */
	font-weight: 500;
	width: 50%;
}

.landing-hero-description {
	color: #6b7280; /* text-gray-500 */
	font-size: 0.875rem; /* text-sm */
	font-weight: 400;
	line-height: 1.75rem; /* leading-7 */
	margin-bottom: 1rem;
	width: 85%;
}

.landing-hero-buttons {
	display: flex;
	align-items: center;
	flex-wrap: wrap;
	gap: 0.75rem;
}

.landing-hero-video-container {
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100vh;
	background: rgba(0, 0, 0, 0.5);
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
	z-index: 100;
	row-gap: 30px;
}

.landing-hero-video-overlay {
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	background: transparent;
	cursor: pointer;
}

.landing-hero-video-wrapper {
	position: relative;
	z-index: 100;
	min-height: 300px;
	width: 90%;
	margin-top: 5rem;
}

.landing-hero-video-close {
	position: absolute;
	right: 0;
	top: -20px;
	height: 2.75rem;
	width: 2.75rem;
	display: flex;
	align-items: center;
	justify-content: center;
	border-radius: 50%;
	background-color: white;
	cursor: pointer;
}

.landing-hero-video-iframe-container {
	border-radius: 1rem;
	overflow: hidden;
	width: 100%;
	height: 100%;
}

.landing-hero-video-iframe {
	height: 100%;
	width: 100%;
}

