    this.getAgentData().then(()=>{
      if(this.agentData.link) this.initQRCode() 
    })
  },
  methods: {
    initQRCode(){
      const element = document.getElementById("qrcode");
      const element2 = document.getElementById("qrcode2");

      if(window.QRCode && (element || element2)) {
        var qrcode = new QRCode(element, {
          text: this.agentData.link,
          width: 100,
          height: 100,
        });

        var qrcode2 = new QRCode(element2, {
          text: this.agentData.link,
          width: 100,
          height: 100,
        });

      }
    },
    async downloadJPGcard1() {
      const element = this.$refs.captureArea1;
      const canvas = await html2canvas(element, { scale: 2, backgroundColor: "#fff" }); // white bg for jpg
      const link = document.createElement("a");
      link.href = canvas.toDataURL("image/jpeg", 0.95); // quality 0–1
     	link.download = this.agentData.referal_id ? `${this.agentData.referal_id}.jpg` : 'Keeping_Referral_Ticket.jpg';
      link.click();
    },  
     async downloadJPGcard2() {
      const element = this.$refs.captureArea2;
      const canvas = await html2canvas(element, { backgroundColor: "#fff" }); // white bg for jpg
      const link = document.createElement("a");
      link.href = canvas.toDataURL("image/jpeg", 0.95); // quality 0–1
      link.download = this.agentData.referal_id ? `${this.agentData.referal_id}.jpg` : 'Keeping_Referral_Ticket.jpg';
      link.click();
    },  
    async getAgentData(){
      try {
        const response = await this.$api.get('v1/get/referal')
        if(response.data) this.agentData = response.data.data

      } catch(e){
        console.log('Failed to fetch agent data: ', e)
      }
    }
  }
}

