components.js:
{
  	data() {
		return {
			userCount: 50,
			year: 2025,
			annualPlan: true,
			monthlyReports: true,
			taxReports: true,
			twoYearPlan: true,
		};
	},
	computed: {
		productPrice() {
			if (this.twoYearPlan) return 412000;
			return this.year >= 2026 ? 824000 : 412000;
		},

		commissionRate() {
			if (this.twoYearPlan) return 0.3;
			return 0.2;
		},

		serviceCount() {
			if (!this.annualPlan) return 0;
			let count = 1;
			if (this.monthlyReports) count++;
			if (this.taxReports) count++;
			return count;
		},

		perUserCommission() {
			if (!this.annualPlan || !this.userCount) return 0;

			const services =
				1 + (this.monthlyReports ? 1 : 0) + (this.taxReports ? 1 : 0);

			const price = this.year >= 2026 ? 824000 : 412000;

			if (this.twoYearPlan && this.year === 2025) {
				const doubled = price * 0.2 * services * 2;
				const extra10 = price * 2 * 0.1;
				return doubled + extra10;
			}

			console.log(this.userCount);
			return this.userCount ? price * 0.2 * services : 0;
		},

		totalRevenue() {
			return this.perUserCommission * this.userCount;
		},

		netIncome() {
			return this.totalRevenue * 0.88;
		},
	},
	methods: {
		openLogin() {
			if (typeof window !== "undefined" && window.open) {
				window.open("https://keeping.uz/public/forms/seller_agent", "_blank");
			} else {
				location.href = "https://keeping.uz/public/forms/seller_agent";
			}
		},
		formatCurrency(value) {
			return new Intl.NumberFormat("uz-UZ", {
				style: "currency",
				currency: "UZS",
				maximumFractionDigits: 0,
			}).format(value);
		},
	},
}

