@keyframes scroll-left {
	0% {
		transform: translateX(0);
	}
	100% {
		transform: translateX(-25%);
	}
}

/* Responsive breakpoints */
@media (min-width: 768px) {
	.agent-testimonial-carousel-container {
		max-width: 99vw;
	}

	.agent-testimonial-carousel-track {
		gap: 0.5rem; /* md:gap-11 */
