  background-color: #f3f3f3;
  border-radius: 0.5rem;
  padding: 6px 12px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 80px;
  height: 45px;
  font-size: 22px;
  transition: background-color 0.2s ease, border-color 0.2s ease;
