components.js:
{
  data() {
    return {
      showModal: false,
      notificationsMessage: [],
      currentIndex: 0
    }
  },

  mounted() {
    // this.getNotificationMessage()
    // if (this.notificationsMessage.length > 0) {
    //   this.showNextModal();
    // }
  },

  computed: {
    currentNotification() {
      return this.notificationsMessage[this.currentIndex] || null;
    }
  },

  methods: {
    async getNotificationMessage(){
      const response = await this.$api.get('v1/get/notification')

      if( response?.data.data ) {
      	this.notificationsMessage = response?.data.data
      }
      console.log('Notifications: ', this.notificationsMessage)
      if( this.notificationsMessage.length ) this.showNextModal();
    },

    showNextModal() {
      if (this.currentIndex < this.notificationsMessage.length) {
        setTimeout(()=>{
          this.showModal = true;
        }, 1000)
      } else {
        this.showModal = false;
      }
    },

    handleRead() {
      this.showModal = false;
      this.currentIndex++;
      this.$nextTick(() => {
        if (this.currentIndex < this.notificationsMessage.length) {
          setTimeout(() => {
            this.showModal = true;
          }, 3000); // short delay for smooth UX
        }
      });
    }
  },
}

