components.js:
{
    data() {
        return {
          	show_refill: true,
            userdata: {
                balance: 0,
                permissions: 0
            },
            orgdata: {
                list: []
            },
          	paymentModal: false,
          	isPaymentModalOpen: false,
          	isAgent: false
        }
    },
    mounted() {
        this.isAgent =  this.$user.extraData.org_type == 99;
        this.getBalance();
        this.getPermission();
        this.getOrgData();
    },
    computed: {
        username() {
            let fullname = this.$user.extraData.full_name
            let username = this.$user.extraData.username

            return fullname || username
        },

        userorgid() {
            let id = this.$user?.orgId?.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ")
            return id || ""
        },

        isAuthorizedUser() {
          const authorizedIds = [
            '56b7a2dc-046d-43e7-b982-843bbc8b5ca9',
          ];
          return authorizedIds.includes(this.$user?.extraData?.user_id);
        }
    },
    methods: {
      	openPaymentModal(){
          this.isPaymentModalOpen = !this.isPaymentModalOpen
          document.addEventListener('keydown', this.handlePressEsc)
        },
        async getBalance() {
            this.show_refill = this.$user.extraData.org_type != 99;
            try {
                let res = await this.$api.get("organizations/balance")
                this.userdata.balance = res.data.data.balance || 0
            } catch (error) {}
        },
        async getOrgData() {
            if (!this.$user.pinfl) {
              if(this.$user.permissions.includes("hr.salary")){
              	await this.$api.get(`v1/realsoft/organizations`).then((res) => {
                    this.orgdata.list = res.data.data.data
                })
            	}
              return;
            }
            if (this.$user.permissions.includes("project.accountant")) {
                await this.$api.get(`v1/accountant/organizations?pinfl=${this.$user.pinfl}`).then((res) => {
                    this.orgdata.list = res.data.data.data
                })
            } else {
                await this.$api.get(`v1/tax/physical/organizations?pinfl=${this.$user.pinfl}`).then((res) => {
                    this.orgdata.list = res.data.data.data.map((v) => {
                        return { ...v, org_type: 100 }
                    })
                    this.orgdata.list.push({ tin: this.$user.pinfl, name: this.$user.fullName, org_type: 100 })
                })
            }
        },
        async getPermission() {
            $api.get(`v2/permissions?user=${this.$user.id}&parent_id=1`).then((res) => {
                this.userdata.permissions = res.data.data.edit_permission.permissions.map((v) => {
                    return v.id
                })
            })
        },

        async showOrganization() {
            window.PlatonMixins.pickUserOrganization(this.$root, this.orgdata.list, {
                closeable: true
            }).then((v) => {
                let theorgData = this.orgdata.list.find((o) => o.tin == v)
                this.$api
                    .post("v1/change/user/organizations", {
                        id: this.$user.id,
                        org_id: theorgData.tin,
                        org_name: theorgData.name,
                        org_type: theorgData.org_type
                    })
                    .then((r) => {
                        location.reload()
                    })
            })
        },
        handlePressEsc(event) {
          if (event.key === 'Escape' && this.isPaymentModalOpen) {
            event.stopPropagation(); 
            this.isPaymentModalOpen = false;
          }
        },  
    }
}

