components.js:
{
	name: "paymentModal",
  data() {
  	return {
    	balance: 0,
      fullname : '',
      isInvalid: false,
      errorMessage: '',
      // isPaymentModalOpen: false,
      isPaymentProssesOpen: false,
      paymentData: { org_id: "", billing_saldo: "0", org_name: "", sum: '', }
    };
  },
  props: {
    isPaymentModalOpen: Boolean
  },
  mounted() {
    this.fullname = this.user.extraData.full_name.substring(0, (this.user.extraData.full_name.length > 30 ? 30 : this.user.extraData.full_name.length));
    this.setPaymentData();
  },
  computed: {
  	user() {
    	return this.$store.getters["platon/user"]
    },
  },
  created() {
  	this.getBalance();
  },
  methods: {
     getLabel(key, fallback) {
      return this.$l(key, fallback);
    },

    formatNumber(value) {
      if (!value) return '';
      return value?.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ' ');
    },
    unformatNumber(value) {
      return value?.replace(/\s/g, '');
    },
    setPaymentData() {
        this.paymentData.org_id = this.user.extraData.org_id;
        this.paymentData.billing_saldo = this.balance;
        this.paymentData.org_name = this.fullname;
    },
    // validateAndSubmit() {
    //   if (this.paymentData.sum.trim() === '') {
    //     this.isInvalid = true;
    //   } else {
    //     this.isInvalid = false;
    //     this.refreshToken();
    //   }
    // },
    validateAndSubmit() {
      const sum = parseInt(this.paymentData.sum.replace(/\s/g, ''), 10);
      if (!sum) {
        this.errorMessage = this.getLabel('incorrectly_amount','Сумма нотўғри киритилган! ');
        this.isInvalid = true;
      } else if (sum < 1000) {
        this.errorMessage = this.getLabel('error.amount_1000','Сумма 1000 дан кам бўлмаслиги керак!');
        this.isInvalid = true;
      } else if (sum > 10000000) {
        this.errorMessage = this.getLabel('error.amount_exceed','Сумма 10 000 000 дан ошмаслиги керак!');
        this.isInvalid = true;
      } else {
        this.isInvalid = false;
        this.refreshToken();
      }
    },
    clearError() {
      if (this.paymentData.sum.trim() !== '') {
        this.isInvalid = false;
      }
    },
    refreshToken() {
      this.$api.post('v1/prod/real-pay/update/token').then((r)=>{
        if(r.data.data.res_data.code == 0) this.openCheckout();
      }).catch((e) =>{
        console.log(e)
      })
    },
    openCheckout() {
      this.$api.post('v1/prod/real-pay/checkout',{
        "amount": this.unformatNumber(this.paymentData.sum)
      }).then((r)=>{
        if(r.data.data.res_data.code == -8){
          refreshToken();
        }else{
          window.open(`https://realpay.uz/checkout/${r.data.data.res_data.data}`, '_blank');
          this.closePaymentProsses();
          this.closePaymentModal();
        }
      }).catch((e) =>{
        console.log(e)
      })
    },
    closePaymentProsses() {
      this.paymentData.sum = '';
      this.isInvalid = false;
      this.isPaymentProssesOpen = false;
    },
    openPaymentProsses() {
      this.isPaymentProssesOpen = true;
    },
    openPaymentModal() {
      this.isPaymentModalOpen = true;
    },
    closePaymentModal() {
      this.isPaymentModalOpen = false;
    },
    async getBalance() {
    	try {
      	let res = await this.$api.get("organizations/balance");
        this.balance = res.data.data.balance || 0;
      	this.paymentData.billing_saldo = this.balance;
      } catch (error) {
      }
    }

  }
}

