          flag_path: "/media/store/landing-v2/assets/svgs/uzbekistan.svg"
				},
				{
					id: 3,
					code: 'ru',
					name: 'Ру',
					flag: "🇷🇺",
          flag_path: "/media/store/landing-v2/assets/svgs/russia.svg"
				}
			]
		}
	},
	mounted() {
		this.outsideClickHandler();
  },
	methods: {
		toggleDropdown() {
			this.isOpen = !this.isOpen;
		},

		selectOption(option) {
			this.isOpen = false;
      console.log('language: ', option.code)
			this.$platonApp.setLocale(option.code);
			location.reload();
		},

		outsideClickHandler() {
			document.addEventListener('click', (event) => {
				if (this.$refs.selectRef && !this.$refs.selectRef.contains(event.target)) {
					this.isOpen = false;
				}
			});

			document.addEventListener('keydown', (event) => {
				if (event.key === 'Escape') {
					this.isOpen = false;
				}
			});
		}
	},
	computed: {
		currentLanguage() {
			let force_language = localStorage.getItem('platon_locale');
			let enable_language = force_language || 'uz';

      console.log('Language: ', enable_language)
			return this.languages.find(item => item.code === enable_language);

		},
		selectedOption() {
			return this.currentLanguage;
		}
	}
}

