}

.billing-realpay-button:hover {
  transform: scale(1.03);
}

.billing-realpay-button:active {
  transform: scale(0.8);
}

/* Bank Details */
.billing-bank-details {
  display: flex;
  flex-direction: column;
  gap: 8px;
  width: 100%;
  flex: 1;
  background: #ffffff;
  border-radius: 8px;
  border: 1px solid #E9E9E9;
