  transform: scale(1.05);
}

.close-modal-button:active {
  transform: scale(0.95);
}

/* Payment Form */
.payment-logo-container {
  text-align: center;
  margin-bottom: 20px;
}

.payment-logo {
  max-width: auto;
  height: 30px;
  opacity: 0.9;
  transition: transform 0.3s, opacity 0.3s;
}

.payment-logo:hover {
  opacity: 1;
}

.billing-payment-form {
