  border: none;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  cursor: pointer;
  font-size: 14px;
  font-style: normal;
  font-weight: 700;
  padding: 5px 20px;
  text-align: center;
  display: flex;
  gap: 5px;
  align-items: center;
  height: 45px;
  border-radius: 12px;
}

.billing-payment-form .back-button:hover {
  background: linear-gradient(45deg, #5a6268, #494f54);
  transform: translateY(-2px);
  box-shadow: 0 6px 10px rgba(0, 0, 0, 0.2);
}

/* Button Group */
.button-group {
  display: flex;
  justify-content: space-between;
  margin-top: 20px;
}

.payment-custom-button {
  padding: 10px 20px;
  font-size: 16px;
  border-radius: 6px;
  transition: background-color 0.3s ease, transform 0.2s ease;
}

.pay-button {
  background-color: #22a8f9;
  border-color: #22a8f9;
  color: #fff;
}

.pay-button:hover {
  background-color: #22a8f9;
  transform: scale(1.05);
}

.back-button {
  background-color: #6c757d;
  border-color: #6c757d;
  color: #fff;
}

.back-button:hover {
  background-color: #5a6268;
  transform: scale(1.05);
}

/* Animation */
@keyframes fadeIn {
  ftest-rom {
    opacity: 0;
    transform: scale(0.9);
  }
  ttest-o {
    opacity: 1;
    transform: scale(1);
  }
}

/* Remove input number spinners */
.payment-custom-input[type=number]::-webkit-outer-spin-button,
.payment-custom-input[type=number]::-webkit-inner-spin-button {
  -webkit-appearance: none;
  margin: 0;
}

.payment-custom-input[type=number] {
  -moz-appearance: textfield;
}

/* Responsive */
@media (max-width: 768px) {
  .payment-modal-content {
    width: 95%;
