				},
				{
					path: "/public/pages/landing-prices-page",
					label: this.$l("landing.navbar.menu.prices","Narx va tariflar"),
				},
			],
		};
	},
	computed: {
      isAuthorizedUser() {
        const authorizedIds = [
          "56b7a2dc-046d-43e7-b982-843bbc8b5ca9",
        ];
        return authorizedIds.includes(this.$user?.extraData?.user_id);
      }
  },
	methods: {
		handleNavigate(path) {
			if (path) {
				const absolutePath = path.startsWith("/") ? path : `/${path}`;
				this.$router.replace(absolutePath);
				this.closeMenu();
			}
		},
		toggleMenu() {
			this.isMenuOpen = !this.isMenuOpen;
		},
		closeMenu() {
			this.isMenuOpen = false;
		},
		handleEscape(event) {
			if (event.key === "Escape" && this.isMenuOpen) {
				this.closeMenu();
			}
		},
		handleResize() {
			if (window.innerWidth >= 768) {
				this.closeMenu();
			}
		},
		openLogin() {
			if (typeof window !== "undefined" && window.open) {

                console.log(location.href)
        window.open(`${location.href}auth/login`, "_blank")
				// window.open("https://keeping.uz/auth/login", "_blank");
			} else {
				location.href = `${location.href}auth/login`;
			}
		},
	},
	mounted() {
		window.addEventListener("resize", this.handleResize);
		document.addEventListener("keydown", this.handleEscape);
	},
	unmounted() {
		window.removeEventListener("resize", this.handleResize);
		document.removeEventListener("keydown", this.handleEscape);
	},
}

