.new-subscription-modal-wrapper {
  width: 100%;
  max-width: 900px;
  max-height: 90vh;
  overflow-y: auto;
}

.new-subscription-modal {
  background-color: #fff;
  border-radius: 16px;
/*   box-shadow: 0 10px 40px rgba(0, 0, 0, 0.2); */
  display: flex;
  flex-direction: column;
}

.new-subscription-modal-header {
  padding: 1.5rem;
  border-bottom: 1px solid #e9ecef;
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.new-subscription-modal-title {
  font-size: 24px;
  font-weight: 600;
  color: #212529;
}

.new-subscription-modal-close {
  background: none;
  border: none;
  padding: 8px;
  cursor: pointer;
  color: #6c757d;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 4px;
  transition: all 0.15s ease-in-out;
}

.new-subscription-modal-close:hover {
  background-color: #f8f9fa;
  color: #212529;
}

.new-subscription-modal-body {
  padding: 24px;
  flex: 1;
  overflow-y: auto;
}

.new-subscription-modal-content {
  display: flex;
  flex-direction: column;
  gap: 24px;
}

.new-subscription-modal-content-main {
  display: flex;
  align-items: flex-start;
  gap: 40px;
  width: 100%;
}


.new-subscription-modal-left {
  flex: 1;
}

.new-subscription-modal-right {
  flex: 1;
}


.new-subscription-modal-price {
  margin: 16px 0 12px 0;
}

.new-subscription-modal-amount {
  font-size: 54px;
  font-weight: 600;
  color: #212529;
  line-height: 1.2;
}

.new-subscription-modal-subtitle {
  color: #6c757d;
  font-size: 16px;
}

.new-subscription-modal-form-group {
  margin-top: 24px;
}

.new-subscription-modal-label {
  display: block;
  font-size: 16px;
  font-weight: 500;
  color: #212529;
  margin-bottom: 4px !important;
}

.new-subscription-modal-select,
.new-subscription-modal-input {
  width: 100%;
  height: 48px;
  padding: 8px 16px;
  border: 1px solid #667085;
  border-radius: 8px !important;
  font-size: 16px;
  transition: border-color 0.3s ease-in-out, box-shadow 0.15s ease-in-out;
  background-color: #fff;
  font-family: inherit;
  box-shadow: 0px 8px 16px -6px rgba(24, 39, 75, 0.08);
}

.new-subscription-modal-select:focus,
.new-subscription-modal-input:focus {
  border-color: #86b7fe;
  outline: 0;
  box-shadow: 0 0 3px 1px rgba(13, 110, 253, 0.45);
}

.new-subscription-modal-payment-cards {
  display: flex;
  padding: 4px;
  background: #F7F8F9;
  border-radius: 12px;
}

.new-subscription-modal-card {
  flex: 1;
  display: flex;
  flex-direction: column;
  padding: 16px;
  height: 160px;
  gap: 14px;
}

.new-subscription-modal-card.second {
  display: flex;
  justify-content: space-between;
  background: white;
  border-radius: 12px;
  border: 1px solid #E9E9E9;
  cursor: pointer;
  transition: all 0.2s ease-in-out;
}

.new-subscription-modal-card.second:hover {
  transform: scale(1.02);
  border: 1px solid #0d6efd;
}

.new-subscription-modal-card-icon {
  flex-shrink: 0;
  width: 32px;
  height: 32px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: #e7f1ff;
  border-radius: 6px;
  color: #0d6efd;
}

.new-subscription-modal-card-content {
  flex: 1;
  display: flex;
  flex-direction: column;
  min-width: 0;
}

.new-subscription-modal-card-label {
  font-size: 12px;
  color: #6c757d;
  margin: 0 0 4px 0;
}

.new-subscription-modal-card-amount {
  font-size: 32px;
  font-weight: 600;
  color: #212529;
  margin: 0;
}

.new-subscription-modal-checkbox-wrapper {
  flex: 1;
  display: flex;
  align-items: center;
  gap: 12px;
}

.new-subscription-modal-checkbox {
  margin-top: 1px;
  width: 18px;
  height: 18px;
  flex-shrink: 0;
  cursor: pointer;
}

.new-subscription-modal-checkbox-label {
  font-size: 14px;
  font-weight: 500;
  color: #495057;
  cursor: pointer;
  line-height: 1.5;
}


.new-subscription-modal-features-title {
  font-size: 14px;
  font-weight: 600;
  color: #212529;
  margin: 0 0 1rem 0;
}

.new-subscription-modal-features-list {
  list-style: none;
  padding: 0;
  margin: 0;
}

.new-subscription-modal-feature-item {
  display: flex;
  align-items: flex-start;
  gap: 12px;
  margin-bottom: 20px;
  font-size: 15px;
  color: #495057;
  line-height: 1.5;
}

.new-subscription-modal-feature-item:last-child {
  margin-bottom: 0;
}

.new-subscription-modal-check-icon {
  flex-shrink: 0;
  color: #0d6efd;
  font-size: 18px;
}

.new-subscription-modal-footer {
  display: flex;
  padding: 24px;
  border-top: 1px solid #e9ecef;
  justify-content: flex-end;
  align-items: center;
  gap: 16px;
}

.new-subscription-modal-cancel-btn {
  background: none;
  border: none;
  color: #6c757d;
  padding: 10px 16px;
  font-size: 16px;
  cursor: pointer;
  text-decoration: none;
  transition: all 0.15s ease-in-out;
}

.new-subscription-modal-cancel-btn:hover {
  color: #495057;
  text-decoration: underline;
}

.new-subscription-modal-submit-btn {
  background-color: #0d6efd;
  border: 1px solid #0d6efd;
  color: #fff;
  padding: 10px 16px;
  font-size: 16px;
  font-weight: 500;
  border-radius: 6px;
  cursor: pointer;
  transition: all 0.15s ease-in-out;
}

.new-subscription-modal-submit-btn:hover:not(:disabled) {
  background-color: #0b5ed7;
  border-color: #0a58ca;
}

.new-subscription-modal-submit-btn-disabled {
 	filter: grayscale(1) 
}

.custom-modal-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 9999;
}


.test-modal-content {
  background: white;
  padding: 20px;
  border-radius: 25px;
  text-align: center;
  width: 340px;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
  position: relative;
  animation: fadeIn 0.3s ease-in-out;
}

/* SUCCESS VA ERROR ICONLARI UCHUN DOIRA */
.test-modal-icon {
  font-style: normal; 
  font-size: 30px;
  margin-bottom: 15px;
  width: 60px;
  height: 60px;
  line-height: 60px;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 50%;
  font-weight: bold;
  color: white;
  margin: 0 auto 15px;
}

.test-modal-title {
  font-size: 20px;
  font-weight: bold;
  margin-bottom: 10px;
}

.test-modal-text {
  font-size: 14px;
  color: #333;
  margin-bottom: 20px;
}

.test-modal-button {
  background: #0d6efd;
  color: white;
  border: none;
  padding: 10px 55px;
  border-radius: 5px;
  cursor: pointer;
  font-size: 16px;
}


.test-close {
  position: absolute;
  top: 10px;
  right: 15px;
  font-size: 20px;
  cursor: pointer;
  font-weight: bold;
}

.success-icon {
  background-color: #28a745;
}

.error-icon {
  background-color: #ff4d4d;
}

@media (max-width: 768px) {
  .new-subscription-modal-content {
    flex-direction: column;
  }

  .new-subscription-modal-left {
    padding-right: 0;
  }

  .new-subscription-modal-right {
    flex: 1;
    padding-left: 0;
    border-left: none;
    border-top: 1px solid #e9ecef;
    padding-top: 2rem;
    margin-top: 2rem;
  }

  .new-subscription-modal-payment-cards {
    flex-direction: column;
  }

  .new-subscription-modal-footer {
    flex-direction: column-reverse;
    align-items: stretch;
  }

  .new-subscription-modal-submit-btn,
  .new-subscription-modal-cancel-btn {
    width: 100%;
    text-align: center;
  }
}

