					// this.isPassportChanged = parsed
					// })
        },

        getCurrencies() {
            this.$api.get("v1/kurs").then((res) => {
                this.currencies = res.data.data || null
            })
        },
        checkFirstVisit() {
          const hasVisited = localStorage.getItem("hasVisited");

          if (!hasVisited) {
            localStorage.setItem("hasVisited", "true");
            // this.showVideo()
          }
        },
        handleEscPress(event) {
          if (event.key === 'Escape' && this.isIframeVisible) {
            event.stopPropagation(); 
            this.closeVideo();
          }
        },
        showVideo(){
          this.isIframeVisible = true;
          document.addEventListener('keydown', this.handleEscPress)
        },
      	closeVideo(){
          this.isIframeVisible = false;
          // window.location.href = 'https://keeping.uz/forms/offer?_target=modal_50';
        },
        async changeLocale(item) {
            this.$platonApp.setLocale(item.code)
        },

      	copySellerAgentReferralCode() {
          const userId = this.$user?.extraData?.user_id;

          if (!userId) {
            this.$bvToast?.toast(this.$l('subscription.modal.error', 'Нусха олишда хатолик'), {
              title: 'Error',
              variant: 'danger',
              solid: true
            });
            return;
          }

          const text = `https://keeping.uz/auth/login?agent_code=${userId}`;

          navigator.clipboard.writeText(text)
            .then(() => {
              this.$bvToast?.toast(this.$l('copied', 'Нусха олинди'), {
                title: 'Copied',
                variant: 'success',
                solid: true
              });
