components.js:
	{
  props: ['isOpen'],
  data() {
    return {
      price: 412000,
      subscriptionSum: 0,
      BHMamount: 0,
      balance: 0,
      selectedMonth: 0,

      referralData: null,
      referralCode: null,

      agreedToTerms: false,
      canSellerAgentBind: false,
      freePlanDisabled: false,
      isPaymentModalOpen: false,
      isReferralCodeInputDisabled: false,       
      referralLoading: false,
      referralDataEmpty: false,
      showModal: false,
      showOfferError: false,
			showPublicOffer: false,

      serviseceModalOpen: false,
      modalType: "success",
      message: null,

      status: 'idle'  ,

      FEATURES_LIST: [
				{title: this.$l("subscription.modal.features.1", "Asosiy vositalar va TMZ hisobi")},
				{title: this.$l("subscription.modal.features.2", "Ish haqi hisoblashlari va xodimlar hisobini yuritish")},
				{title: this.$l("subscription.modal.features.3", "Soliq hisobotlarini avtomatik shakllantrish")},
				{title: this.$l("subscription.modal.features.4", "Ijara va tashkilotlar bilan hisob-kitoblar")},
				{title: this.$l("subscription.modal.features.5", "Ta'sischilar bilan aniq hisob-kitoblar")},
      ],
    }
  },

  mounted() {
    this.getBillingData();
    this.selectedMonth = this.freePlanDisabled ? 12 : 0
    if(this.referralCode) this.getReferralData(this.referralCode);
  },

  computed: {
    paymentAmount() {
      const price   = Number(this.price) || 0;
      const bhm     = Number(this.BHMamount) || 0;
      const balance = Number(this.balance) || 0;
      const v = Math.max(0, price * bhm - balance);
      return Number.isFinite(v) ? v : 0;
    },
		displayValue() {
      if (this.status === 'ok' && this.referralData?.full_name) {
        return `${this.referralData.full_name} - ${this.referralCode}`
      }
      if (this.status === 'not_found') return this.$l('subscription.referral.not.found' ,"Agent topilmadi - ") + this.referralCode
      if (this.status === 'error') return this.$l('subscription.modal.error', 'Xatolik!');

      return this.referralCode
    },
    placeholder() {
      if (this.status === 'ok' && this.referralData?.full_name) {
        return `${this.referralData.full_name} - ${this.referralCode}`
      }
      return this.$l("subcription.referral.code.placeholder", "Реферрал кодини киритинг: 0000-00000")
    },
    isDisabled() {
      const amount = Number(this.paymentAmount > 0 || 0);

      const agreed =
        this.agreedToTerms === true 

      return amount > 0 || !agreed;
    }
  },

  watch: {
    referralCode(newValue) {

    },
    selectedMonth(newValue){
      this.BHMamount = newValue / 12;
    },
    freePlanDisabled(newValue){
      this.selectedMonth = this.freePlanDisabled ? 12 : 0
    },
  },

  methods: {
    async getBillingData(){
      await this.$api.get('v1/page/data/billing').then((res)=>{
        const response = res.data.data

        this.canSellerAgentBind = response?.seller_agent_bind?.can_bind
        this.freePlanDisabled = response.disable_card?.case == "DISABLE" ? true : false;
        this.referralData = response.seller_agent[0]
        this.referralCode = response.seller_agent[0]?.code
        if(this.referralCode) this.getReferralData(this.referralCode)
        this.balance = response.billing_balance.balance
      });
    },

    async getReferralData(code) {
      this.referralDataEmpty = false;
      this.referralLoading = true;

      try {
        const res = await this.$api.get(`v1/seller-agent/info?code=${code}`);
        const data = res.data?.data;

        if (data?.code) {
          this.referralData = data;
          this.status = 'ok'
          this.isReferralCodeInputDisabled = true
          this.referralDataEmpty = false;
        } else {
          this.status = 'not_found'
          this.isReferralCodeInputDisabled = true
          this.referralData = {};
          this.referralDataEmpty = true;
        }
      } catch (error) {
        console.error('Error fetching referral data:', error);
        this.referralDataEmpty = true;
        this.referralData = null
        this.status = 'error'
        this.isReferralCodeInputDisabled = true
      } finally {
        this.referralLoading = false;
      }
    },

    async acceptTerms() {
      let org_id = this.$user.extraData.org_id ? this.$user.extraData.org_id : this.$user.extraData.pinfl;
      var signList = [`${this.$user.extraData.org_id}`];

      await $api.get(`uz/organizations/binding?org_id=${org_id}`)
        .then((data) => {

          this.$platonApp.signWithEIMZO(JSON.stringify(data.data.data), (signedPkcs7, loadedKey) => {
            $api.post(`v1/uz/organizations/binding`, {"sign": signedPkcs7, "org_id": org_id})
              .then((res) => {
              	if(res.data.status == 200) {

                  this.subscribe()
                  console.log('Оферта тасдиқланди. Кабинет фаоллаштирилмоқда!')
                } else {
                  alert(res.message)
                }
              }).catch(err=> {
              		 console.log(err.response.data.message)
              	// this.$form.errorToast(err.response.data.message);
            });
          }, { closed: true, isDataBase64: false, whiteList: signList});
        }).catch(err=> {
        		 console.log(err.response.data.message)
        	// this.$form.errorToast(err.response.data.message);
      });
    },

    subscribe() {
      const isFreeType = this.selectedMonth === 0;
      const month = this.selectedMonth === 0 ? 1 : this.selectedMonth
      const price = isFreeType ? 0 : this.price;
      const totalSum = isFreeType ? 0 : this.subscriptionSum;

      const data = {
        bdate: this.billingStartDate(),
        edate: this.billingEndDate(month),
        org_id: this.$user.extraData.org_id,
        user_id: this.$user.id,
        is_active: true,
        type: isFreeType ? 'FREE' : 'SUBSCRIPTION',
        modul_id: 100,
        month,
        price,
        total_sum: totalSum,
        bdate_dis: false,
        is_active_dis: true,
        is_disabled: false,
        month_dis: false,
        seller_agent_id: this.canSellerAgentBind ? this.referralData?.seller_agent_id : null
      };


      $api.post('v4/billing/modules', { save: data })
      .then(response => {

        if (response.data.data.status === 200) {
          this.modalType = 'success'
          this.message = response.data.data.massage

          this.serviseceModalOpen = true;
        } else {
          this.message = response.data.data.massage
          this.modalType = 'error'
          this.serviseceModalOpen = true;
        }
      })
      .catch(e => {
        console.error('Хатолик:', e);
        this.$bvToast.toast('Сақлашда хатолик юз берди!', {
          title: 'Хатолик!',
          variant: 'danger',
          solid: true,
        });
      }).finally(()=>{
          setTimeout(() => {
            // window.location.reload();
          }, 1000)
      });
    },

    handleOpenPaymentModal(){
      if(this.getPaymentAmount() > 0) this.isPaymentModalOpen = true
    },

    getOptions() {
      const freePlanOptions = [
              { value: 0, text: this.$l("subscription.modal.options.0", "1 oy - Bepul") },
              { value: 12, text: this.$l("subscription.modal.options.1", "1 yillik") },
              { value: 24, text: this.$l("subscription.modal.options.2", "2 yillik") }
            ];

      const paidPlanOptions = [
              { value: 12, text: this.$l("subscription.modal.options.1", "1 yillik") },
              { value: 24, text: this.$l("subscription.modal.options.2", "2 yillik") }
            ] 

      return this.freePlanDisabled ? paidPlanOptions : freePlanOptions
    },

    onInput(event) {
      if (this.isReferralCodeInputDisabled) return
      this.referralCode = event.target.value

      if (this.referralCode.length === 10) {
        this.getReferralData(this.referralCode)
      }
    },

    resetInput () {
      this.isReferralCodeInputDisabled = false
      this.status = 'idle'
      this.referralData = null
      this.referralCode = ''
      this.$nextTick(() => {
        const el = this.$el.querySelector('input[name="referral"]')
        el && el.focus()
      })
    },

    calculateSubscription(cardId) {
      if (!!this.selectedMonth) {
        if (cardId === 146) {
          const years = this.selectedMonth / 12;
          this.subscriptionSum = this.price * years;
        }
      }
    },

    formatWithSpaces(number) {
      return number?.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ' ');
    },

    getPaymentAmount(){
      const saldo = Math.max(0, (this.price * this.BHMamount) - this.balance)
      return saldo;
    },

    closeModal() {

      this.$emit('close');
      this.isPaymentModalOpen = false;
      window.location.reload()
    },

    async handleSubmit() {
      if (!this.$user.extraData.offer && !this.agreedToTerms) {
        this.showOfferError = true;
        console.log('Not agreed offer')

        this.$bvToast.toast(this.$l("subscription.modal.error.offer.v2", "«Ommaviy oferta bilan tanishdim» tugmasini bosing"), {
          title: this.$l('subscription.modal.error', 'Xatolik!'),
          variant: 'danger',
          solid: true,
        });

        return;
      }

      if(this.getPaymentAmount() > 0) {
      	this.$bvToast.toast(this.$l("subscription.modal.error.low.balance", 'Hisobingizda yetarli mablag mavjud emas'), {
          title: this.$l('subscription.modal.error', 'Xatolik!'),
          variant: 'danger',
          solid: true,
        });

        return;
      }

      !this.$user.extraData.offer ? await this.acceptTerms() : this.subscribe()
    },

    billingStartDate() {
      const today = new Date();
      const year = today.getFullYear();
      const month = String(today.getMonth() + 1).padStart(2, '0');
      const day = String(today.getDate()).padStart(2, '0');
      return `${year}-${month}-${day}`;
    },

    billingEndDate(selectedMonth) {
      const today = new Date();
      const endDate = new Date(today.setMonth(today.getMonth() + selectedMonth));
      const year = endDate.getFullYear();
      const month = String(endDate.getMonth() + 1).padStart(2, '0');
      const day = String(endDate.getDate()).padStart(2, '0');
      return `${year}-${month}-${day}`;
    },
  }
}

